import React, { useRef } from "react";
import "./contact.css";
import resumeImage from "../../assets/resume.png";
import LinkedInIcon from "../../assets/linkedin.png";
import GitHubIcon from "../../assets/github.png";
import emailjs from '@emailjs/browser';

const Contact = () => {
  const form = useRef();
  const [ showResumeModal, setShowResumeModal ] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm('service_76xwzv5', 'template_39ulmnw', form.current, {
        publicKey: process.env.REACT_APP_EMAILJS_PUBLIC_KEY,
      })
      .then(
        () => {
          console.log('SUCCESS!');
          form.current.reset();
        },
        (error) => {
          console.log('FAILED...', error.text);
        },
      );
  };
  return (
    <section id="contactPage">
      <div id="resume">
        <h1 className="resumePageTitle">Resume</h1>
        <span className="resumeDesc">Click the image below to view my resume</span>
        <img src={resumeImage} alt="Resume" className="resumeImage" onClick={() => setShowResumeModal(true)} />
        {showResumeModal && (
          <div className="resumeModal">
            <div className="resumeModalContent">
              <span className="close" onClick={() => setShowResumeModal(false)}>&times;</span>
              <iframe src="https://drive.google.com/file/d/1e1mQ8v0Yv7LXyqQ4FbZK0Q8q1L5nqJQy/preview" title="Resume" className="resumeIframe"></iframe>
            </div>
          </div>
        )}
      </div>
      <div id="contact">
        <h1 className="contactPageTitle">Contact Me</h1>
        <span className="contactDesc">Please fill out the form below to discuss any work opportunities</span>
        <form className="contactForm" ref={form} onSubmit={sendEmail}>
          <input type="text" className="name" placeholder="Your Name" name='from_name' />
          <input type="email" className="email" placeholder="Your Email" name='from_email'/>
          <textarea className="msg" name="message" rows="5" placeholder="Your Message"></textarea>
          <button type="submit" value="Send" className="submitBtn">Submit</button>
          <div className="links">
            <a href="https://www.linkedin.com/in/isaac-thill" target="_blank" rel="noreferrer">
              <img src={LinkedInIcon} alt="LinkedIn" className="link" />
            </a>
            <a href="https://www.github.com/ithill22" target="_blank" rel="noreferrer">
              <img src={GitHubIcon} alt="GitHub" className="link" />
            </a>
          </div>
        </form>
      </div>
    </section>
  )
}

export default Contact;
